import {useState} from "react";
import bookLuv2CodeImage from './../../Images/BooksImages/book-luv2code-1000.png';
import newBook2Image from './../../Images/BooksImages/new-book-2.png';
import newBook1Image from './../../Images/BooksImages/new-book-1.png';

const books = [
    {src: bookLuv2CodeImage, alt: "book 1"},
    {src: newBook2Image, alt: "book 2"},
    {src: newBook1Image, alt: "book 3"}
];

export const Carousel = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const handlePrev = () => {
        setCurrentSlide((prevSlide) => (prevSlide === 0 ? books.length - 1 : prevSlide - 1));
    };

    const handleNext = () => {
        setCurrentSlide((prevSlide) => (prevSlide === books.length - 1 ? 0 : prevSlide + 1));
    };

    return (
        <div className="container mx-auto mt-5" style={{height: 550}}>
            <div className="text-center">
                <h3 className="text-xl font-semibold">Find your next "I stayed up too late reading" book.</h3>
            </div>
            <div className="hidden lg:block mt-5 relative">
                {/* Desktop */}
                <div className="relative w-full overflow-hidden">
                    <div className="flex transition-transform duration-300"
                         style={{transform: `translateX(-${currentSlide * 100}%)`}}>
                        {books.map((book, index) => (
                            <div key={index}
                                 className={`w-full flex justify-center ${index === currentSlide ? 'block' : 'hidden'}`}>
                                <img src={book.src} alt={book.alt} className="w-1/3"/>
                            </div>
                        ))}
                    </div>
                </div>
                <button
                    className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-blue-600 text-white p-2 rounded-full"
                    type="button"
                    onClick={handlePrev}
                >
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="sr-only">Previous</span>
                </button>
                <button
                    className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-blue-600 text-white p-2 rounded-full"
                    type="button"
                    onClick={handleNext}
                >
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="sr-only">Next</span>
                </button>
            </div>

            {/* Mobile */}
            <div className="lg:hidden mt-3">
                <div className="flex justify-center">
                    <img src={books[currentSlide].src} alt={books[currentSlide].alt} className="w-2/3"/>
                </div>
            </div>
            <div className="text-center mt-3">
                <a
                    className="inline-block bg-blue-600 text-white font-semibold py-2 px-4 rounded hover:bg-blue-700 transition duration-300"
                    href="/search"
                >
                    View More
                </a>
            </div>
        </div>
    );
};
