import {useEffect, useState} from "react";

export const Carousel = () => {
    //const [books, setBooks] = useState<BookModel[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [httpError, setHttpError] = useState(null);

    useEffect(() => {
        const fetchBooks = async () => {
            const baseUrl: string = "http://localhost:8080/api/books";
            const url: string = `${baseUrl}?page=0&size=9`;

            const response = await fetch(url);

            if (!response.ok) {
                throw new Error('Something went wrong!');
            }

            const responseJson = await response.json();
            const responseData = responseJson._embedded.books;

            //const loadedBooks: BookModel[] = [];

            for (const key in responseData) {
                // loadedBooks.push({
                //     id: responseData[key].id,
                //     title: responseData[key].title,
                //     author: responseData[key].author,
                //     description: responseData[key].description,
                //     copies: responseData[key].copies,
                //     copiesAvailable: responseData[key].copiesAvailable,
                //     category: responseData[key].category,
                //     img: responseData[key].img,
                // });
            }

            // setBooks(loadedBooks);
            setIsLoading(false);
        };

        fetchBooks().catch((error: any) => {
            setIsLoading(false);
            setHttpError(error.message);
        });
    }, []);

    if (isLoading) {
        // return <SpinnerLoading />;
    }

    if (httpError) {
        return (
            <div className="container mx-auto mt-5">
                <p>{httpError}</p>
            </div>
        );
    }

    return (
        <div className="container mx-auto mt-5" style={{height: 550}}>
            <div className="text-center">
                <h3 className="text-xl font-semibold">Find your next "I stayed up too late reading" book.</h3>
            </div>
            <div className="hidden lg:block mt-5">
                {/* Desktop */}
                <div className="relative w-full overflow-hidden">
                    <div className="flex transition-transform duration-300">
                        {/*{books.slice(0, 3).map((book, index) => (*/}
                        {/*    <div key={index} className="w-1/3 flex justify-center">*/}
                        {/*        <ReturnBook book={book} key={book.id} />*/}
                        {/*    </div>*/}
                        {/*))}*/}
                    </div>
                    <div className="flex transition-transform duration-300">
                        {/*{books.slice(3, 6).map((book, index) => (*/}
                        {/*    <div key={index} className="w-1/3 flex justify-center">*/}
                        {/*        <ReturnBook book={book} key={book.id} />*/}
                        {/*    </div>*/}
                        {/*))}*/}
                    </div>
                    <div className="flex transition-transform duration-300">
                        {/*{books.slice(6, 9).map((book, index) => (*/}
                        {/*    <div key={index} className="w-1/3 flex justify-center">*/}
                        {/*        <ReturnBook book={book} key={book.id} />*/}
                        {/*    </div>*/}
                        {/*))}*/}
                    </div>
                </div>
                <button
                    className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-blue-600 text-white p-2 rounded-full"
                    type="button"
                    data-bs-target="#carouselExampleControls"
                    data-bs-slide="prev"
                >
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="sr-only">Previous</span>
                </button>
                <button
                    className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-blue-600 text-white p-2 rounded-full"
                    type="button"
                    data-bs-target="#carouselExampleControls"
                    data-bs-slide="next"
                >
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="sr-only">Next</span>
                </button>
            </div>

            {/* Mobile */}
            <div className="lg:hidden mt-3">
                <div className="flex justify-center">
                    {/*<ReturnBook book={books[0]} key={books[0].id} />*/}
                </div>
            </div>
            <div className="text-center mt-3">
                <a
                    className="inline-block bg-blue-600 text-white font-semibold py-2 px-4 rounded hover:bg-blue-700 transition duration-300"
                    href="/search"
                >
                    View More
                </a>
            </div>
        </div>
    );
};
